import { useMemo, useState } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const toAr = n => (Math.round(Number(n) || 0)).toLocaleString("fr-FR").replace(/\u202F/g, " ");

export default function App() {
  const [logoDataUrl, setLogoDataUrl] = useState(null);
  const [rows, setRows] = useState([]);

  const [f, setF] = useState({
    // EMPLOYEUR
    employeur_nom: "", employeur_adresse: "", employeur_nif: "",
    employeur_stat: "", employeur_cnaps: "",
    // EMPLOYÉ
    nom: "", prenom: "", cin: "", matricule: "", poste: "", date_embauche: "",
    // PÉRIODE
    mois: "", annee: "",
    // RÉMUNÉRATIONS
    salaire_base: "",
    heures_sup: "",
    prime_transport: "",
    prime_logement: "",
    prime_panier: "",
    prime_autre: "",
    avantages: "",
    retenues: "",
    nb_enfants: ""
  });

  const hints = {
    salaire_base: "Montant mensuel de base (Ar). Ex: 400000",
    heures_sup: "Montant total des heures sup (Ar). Ex: 25000",
    prime_transport: "Prime de transport (Ar)",
    prime_logement: "Prime de logement (Ar)",
    prime_panier: "Prime de panier (Ar)",
    prime_autre: "Autres primes/indemnités (Ar)",
    avantages: "Avantages en nature (logement, voiture, téléphone...)",
    retenues: "Retenues (avances, prêts, pénalités...)",
    nb_enfants: "Nombre d'enfants à charge (max 4 pour abattement IRSA)"
  };

  const calc = useMemo(() => {
    const salaire_base = +f.salaire_base || 0;
    const heures_sup = +f.heures_sup || 0;
    const prime_transport = +f.prime_transport || 0;
    const prime_logement = +f.prime_logement || 0;
    const prime_panier = +f.prime_panier || 0;
    const prime_autre = +f.prime_autre || 0;
    const avantages = +f.avantages || 0;
    const retenues = +f.retenues || 0;
    const nb_enfants = Math.min(+f.nb_enfants || 0, 4);

    const total_primes = prime_transport + prime_logement + prime_panier + prime_autre;
    const brut = salaire_base + heures_sup + total_primes + avantages;

    const cnaps = brut * 0.01;
    const ostie = brut * 0.01;

    const abattement = nb_enfants * 2000;
    let baseIrsa = Math.max(brut - abattement, 0);
    let irsa = 0;
    if (baseIrsa > 600000) { irsa += (baseIrsa - 600000) * 0.20; baseIrsa = 600000; }
    if (baseIrsa > 500000) { irsa += (baseIrsa - 500000) * 0.15; baseIrsa = 500000; }
    if (baseIrsa > 400000) { irsa += (baseIrsa - 400000) * 0.10; baseIrsa = 400000; }
    if (baseIrsa > 350000) { irsa += (baseIrsa - 350000) * 0.05; }
    irsa = Math.max(irsa, 3000);

    const net = brut - cnaps - ostie - irsa - retenues;
    return { brut, cnaps, ostie, irsa, net, abattement, total_primes, prime_transport, prime_logement, prime_panier, prime_autre };
  }, [f]);

  const reset = () =>
    setF({
      employeur_nom: "", employeur_adresse: "", employeur_nif: "",
      employeur_stat: "", employeur_cnaps: "",
      nom: "", prenom: "", cin: "", matricule: "", poste: "", date_embauche: "",
      mois: "", annee: "",
      salaire_base: "", heures_sup: "",
      prime_transport: "", prime_logement: "", prime_panier: "", prime_autre: "",
      avantages: "", retenues: "", nb_enfants: ""
    });

  const addRow = () => {
    const r = { ...f, ...calc };
    setRows(prev => [r, ...prev]);
    reset();
  };

  const totalMasse = rows.reduce((s, r) => s + (r.net || 0), 0);

  const exportPDF = (r) => {
    const doc = new jsPDF({ unit: "pt", format: "a4" });
    const marginX = 40;
    const marginY = 36;

    if (logoDataUrl) {
      doc.addImage(logoDataUrl, "PNG", marginX, marginY - 10, 50, 50);
      doc.setLineWidth(2);
      doc.setDrawColor(180, 180, 180);
      doc.circle(marginX + 25, marginY + 15, 25);
    }

    doc.setFontSize(16);
    doc.text("BULLETIN DE PAIE", 297, marginY + 10, { align: "center" });

    // Employeur
    autoTable(doc, {
      startY: marginY + 40,
      theme: "grid",
      styles: { fontSize: 9, cellPadding: 4 },
      head: [['EMPLOYEUR', 'INFORMATIONS']],
      body: [
        ['Raison sociale', r.employeur_nom || '-'],
        ['Adresse', r.employeur_adresse || '-'],
        ['NIF / STAT / CNAPS', `${r.employeur_nif || '-'} / ${r.employeur_stat || '-'} / ${r.employeur_cnaps || '-'}`]
      ]
    });

    // Employé / Période
    autoTable(doc, {
      startY: doc.lastAutoTable.finalY + 10,
      theme: "grid",
      styles: { fontSize: 9, cellPadding: 4 },
      head: [['EMPLOYÉ', 'PÉRIODE']],
      body: [
        [
          `Nom: ${r.nom || '-'} ${r.prenom || '-'}\nCIN: ${r.cin || '-'}\nMatricule: ${r.matricule || '-'}\nPoste: ${r.poste || '-'}\nDate d'embauche: ${r.date_embauche || '-'}`,
          `Mois/Année: ${r.mois || '-'} / ${r.annee || '-'}`
        ]
      ]
    });

    // Rémunérations détaillées
    const lines = [
      ['Salaire de base', toAr(r.salaire_base), toAr(r.salaire_base), ''],
      ['Heures supplémentaires', toAr(r.heures_sup), toAr(r.heures_sup), ''],
      ['Prime de transport', toAr(r.prime_transport), toAr(r.prime_transport), ''],
      ['Prime de logement', toAr(r.prime_logement), toAr(r.prime_logement), ''],
      ['Prime de panier', toAr(r.prime_panier), toAr(r.prime_panier), ''],
      ['Autres primes/indemnités', toAr(r.prime_autre), toAr(r.prime_autre), ''],
      ['Avantages en nature', toAr(r.avantages), toAr(r.avantages), ''],
      [{ content: 'TOTAL BRUT', styles: { fontStyle: 'bold' } }, '', toAr(r.brut), ''],
      ['Retenues diverses', toAr(r.retenues), `-${toAr(r.retenues)}`, ''],
      ['CNAPS (1%)', '', `-${toAr(r.cnaps)}`, ''],
      ['OSTIE (1%)', '', `-${toAr(r.ostie)}`, ''],
      [`IRSA (abattement ${toAr(r.abattement)} Ar)`, '', `-${toAr(r.irsa)}`, ''],
      [{ content: 'NET A PAYER', styles: { fontStyle: 'bold' } }, '', toAr(r.net), '']
    ];

    autoTable(doc, {
      startY: doc.lastAutoTable.finalY + 10,
      theme: "grid",
      styles: { fontSize: 9, cellPadding: 3 },
      head: [['Désignation', 'Base', 'Part Salariale', 'Part Patronale']],
      body: lines
    });

    doc.save(`fiche_paie_${(r.nom||'')}_${(r.prenom||'')}.pdf`);
  };

  const onLogoChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) return setLogoDataUrl(null);
    const reader = new FileReader();
    reader.onload = () => setLogoDataUrl(reader.result);
    reader.readAsDataURL(file);
  };

  const set = (k) => (e) => setF(s => ({ ...s, [k]: e.target.value }));

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-6">
      <h1 className="text-2xl font-bold">Bulletin de paie — Madagascar</h1>

      {/* Upload Logo */}
      <div>
        <label>Logo de l’entreprise</label>
        <input type="file" accept="image/*" onChange={onLogoChange} />
        {logoDataUrl && <img src={logoDataUrl} alt="Logo" style={{ width: 64, height: 64, borderRadius: '50%' }} />}
      </div>

      {/* Formulaire */}
      <div className="grid md:grid-cols-2 gap-4">
        <div>
          <h2>Informations Employeur</h2>
          <input placeholder="Raison sociale" value={f.employeur_nom} onChange={set("employeur_nom")} />
          <input placeholder="Adresse" value={f.employeur_adresse} onChange={set("employeur_adresse")} />
          <input placeholder="NIF" value={f.employeur_nif} onChange={set("employeur_nif")} />
          <input placeholder="STAT" value={f.employeur_stat} onChange={set("employeur_stat")} />
          <input placeholder="CNAPS" value={f.employeur_cnaps} onChange={set("employeur_cnaps")} />
        </div>

        <div>
          <h2>Informations Employé</h2>
          <input placeholder="Nom" value={f.nom} onChange={set("nom")} />
          <input placeholder="Prénom" value={f.prenom} onChange={set("prenom")} />
          <input placeholder="CIN" value={f.cin} onChange={set("cin")} />
          <input placeholder="Matricule" value={f.matricule} onChange={set("matricule")} />
          <input placeholder="Poste" value={f.poste} onChange={set("poste")} />
          <input type="date" value={f.date_embauche} onChange={set("date_embauche")} />
          <input placeholder="Mois" value={f.mois} onChange={set("mois")} />
          <input placeholder="Année" value={f.annee} onChange={set("annee")} />
        </div>

        <div className="md:col-span-2">
          <h2>Salaire & Composants</h2>
          <input type="number" placeholder={hints.salaire_base} value={f.salaire_base} onChange={set("salaire_base")} />
          <input type="number" placeholder={hints.heures_sup} value={f.heures_sup} onChange={set("heures_sup")} />
          <input type="number" placeholder={hints.prime_transport} value={f.prime_transport} onChange={set("prime_transport")} />
          <input type="number" placeholder={hints.prime_logement} value={f.prime_logement} onChange={set("prime_logement")} />
          <input type="number" placeholder={hints.prime_panier} value={f.prime_panier} onChange={set("prime_panier")} />
          <input type="number" placeholder={hints.prime_autre} value={f.prime_autre} onChange={set("prime_autre")} />
          <input type="number" placeholder={hints.avantages} value={f.avantages} onChange={set("avantages")} />
          <input type="number" placeholder={hints.retenues} value={f.retenues} onChange={set("retenues")} />
          <input type="number" placeholder={hints.nb_enfants} value={f.nb_enfants} onChange={set("nb_enfants")} />
        </div>
      </div>

      <button onClick={addRow}>Ajouter la fiche</button>

      {/* Dashboard */}
      <h2>Dashboard des fiches</h2>
      <table>
        <thead>
          <tr>
            <th>Employé</th>
            <th>Brut</th>
            <th>Net</th>
            <th>PDF</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.nom} {r.prenom}</td>
              <td>{toAr(r.brut)} Ar</td>
              <td>{toAr(r.net)} Ar</td>
              <td><button onClick={() => exportPDF(r)}>Export</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>Total masse salariale nette : {toAr(totalMasse)} Ar</div>
    </div>
  );
}
