import React, { useMemo, useState } from "react";
import { exportPDF as defaultExportPDF, toAr as defaultToAr } from "./utils";
import { Link } from 'react-router-dom';
// Le composant accepte maintenant `rows` et `totalMasse` en props (avec valeurs par défaut).
// Si vous préférez utiliser un context global, créez et exportez `RowsContext` puis remplacez l'usage.
export default function Dashboard({ rows = [], totalMasse = 0, exportPDF = defaultExportPDF, toAr = defaultToAr }) {
  // Si aucune rows passée en props, tenter de lire depuis localStorage (persistance depuis App.jsx)
  let localRows = rows;
  if ((!rows || rows.length === 0) && typeof window !== 'undefined') {
    try {
      const raw = localStorage.getItem('rows');
      localRows = raw ? JSON.parse(raw) : [];
    } catch (e) {
      localRows = [];
    }
  }
  const displayRows = localRows || [];
  const displayTotal = totalMasse || displayRows.reduce((s, r) => s + (r.net || 0), 0);

  // Recherche et filtres
  const [query, setQuery] = useState("");
  const [filterMonth, setFilterMonth] = useState("");
  const [filterYear, setFilterYear] = useState("");

  const months = useMemo(() => {
    return Array.from(new Set(displayRows.map(r => r.mois).filter(Boolean)));
  }, [displayRows]);
  const years = useMemo(() => {
    return Array.from(new Set(displayRows.map(r => r.annee).filter(Boolean)));
  }, [displayRows]);

  const filteredRows = useMemo(() => {
    const q = (query || "").trim().toLowerCase();
    return displayRows.filter(r => {
      if (q) {
        const inName = (`${r.nom || ""} ${r.prenom || ""}`).toLowerCase().includes(q);
        const inPoste = (r.poste || "").toLowerCase().includes(q);
        const inPeriod = `${r.mois || ""}/${r.annee || ""}`.toLowerCase().includes(q);
        if (!inName && !inPoste && !inPeriod) return false;
      }
      if (filterMonth && r.mois !== filterMonth) return false;
      if (filterYear && r.annee !== filterYear) return false;
      return true;
    });
  }, [displayRows, query, filterMonth, filterYear]);

  const totalCount = displayRows.length;
  const filteredCount = filteredRows.length;
  const totalNet = displayRows.reduce((s, r) => s + (r.net || 0), 0);
  const filteredNet = filteredRows.reduce((s, r) => s + (r.net || 0), 0);

  return (
    <div className="section">
      <div className="flex items-center justify-between mb-3">
        <div className="space-x-2">
          <Link to="/fiche" className="btn btn-primary">Aller au formulaire</Link>
          <Link to="/" className="text-sm text-gray-600">Accueil</Link>
        </div>
      </div>
      <div className="flex items-center justify-between mb-3">
        <div className="h2">Dashboard des fiches</div>
        <div className="text-sm font-semibold">Masse salariale nette : {toAr(displayTotal)} Ar</div>
      </div>

      {/* Summary board */}
      <div className="grid grid-cols-3 gap-4 mb-4">
        <div className="section"><div className="font-medium">Total fiches</div><div className="text-lg font-semibold">{totalCount}</div></div>
        <div className="section"><div className="font-medium">Fiches affichées</div><div className="text-lg font-semibold">{filteredCount}</div></div>
        
      </div>

      {/* Filters */}
      <div className="flex items-center gap-3 mb-3">
        <input
          placeholder="Rechercher par nom, poste, période..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          className="input"
        />
        <select value={filterMonth} onChange={e => setFilterMonth(e.target.value)} className="input">
          <option value="">Tous les mois</option>
          {months.map(m => <option key={m} value={m}>{m}</option>)}
        </select>
        <select value={filterYear} onChange={e => setFilterYear(e.target.value)} className="input">
          <option value="">Toutes les années</option>
          {years.map(y => <option key={y} value={y}>{y}</option>)}
        </select>
        <button className="btn" onClick={() => { setQuery(""); setFilterMonth(""); setFilterYear(""); }}>Réinitialiser</button>
      </div>
      <div className="overflow-auto">
        <table className="table">
          <thead>
            <tr>
              <th>Employé</th>
              <th>Poste</th>
              <th>Période</th>
              <th>Brut</th>
              <th>CNAPS</th>
              <th>OSTIE</th>
              <th>IRSA</th>
              <th>Net</th>
              <th>PDF</th>
            </tr>
          </thead>
          <tbody>
            {filteredRows.map((r, i) => (
              <tr key={i}>
                <td>
                  {r.nom} {r.prenom}
                </td>
                <td>{r.poste}</td>
                <td>
                  {r.mois}/{r.annee}
                </td>
                <td>{toAr(r.brut)} Ar</td>
                <td>{toAr(r.cnaps)} Ar</td>
                <td>{toAr(r.ostie)} Ar</td>
                <td>{toAr(r.irsa)} Ar</td>
                <td className="font-semibold">{toAr(r.net)} Ar</td>
                <td>
                  <button
                    className="btn btn-success"
                    onClick={() => exportPDF(r)}
                  >
                    Export
                  </button>
                </td>
              </tr>
            )
              )
                }
            {filteredRows.length === 0 && (
              <tr>
                <td
                  colSpan="9"
                  className="text-center text-gray-500 py-6"
                >
                  Aucune fiche pour l’instant.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      <p className="text-xs text-gray-500">
        Barèmes et taux simplifiés à adapter selon votre convention/actualités
        (CNAPS, OSTIE, IRSA).
      </p>
    </div>
  );
}

const Card = () => {
  return (
    <div className="group relative flex w-80 flex-col rounded-xl bg-slate-950 p-4 shadow-2xl transition-all duration-300 hover:scale-[1.02] hover:shadow-indigo-500/20">
      <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 opacity-20 blur-sm transition-opacity duration-300 group-hover:opacity-30" />
      <div className="absolute inset-px rounded-[11px] bg-slate-950" />
      <div className="relative">
        <div className="mb-4 flex items-center justify-between">
          <div className="flex items-center gap-2">
            <div className="flex h-8 w-8 items-center justify-center rounded-lg bg-gradient-to-br from-indigo-500 to-purple-500">
              <svg className="h-4 w-4 text-white" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7h8m0 0v8m0-8l-8 8-4-4-6 6" />
              </svg>
            </div>
            <h3 className="text-sm font-semibold text-white">Performance Analytics</h3>
          </div>
          <span className="flex items-center gap-1 rounded-full bg-emerald-500/10 px-2 py-1 text-xs font-medium text-emerald-500">
            <span className="h-1.5 w-1.5 rounded-full bg-emerald-500" />
            Live
          </span>
        </div>
        <div className="mb-4 grid grid-cols-2 gap-4">
          <div className="rounded-lg bg-slate-900/50 p-3">
            <p className="text-xs font-medium text-slate-400">Total Views</p>
            <p className="text-lg font-semibold text-white">24.5K</p>
            <span className="text-xs font-medium text-emerald-500">+12.3%</span>
          </div>
          <div className="rounded-lg bg-slate-900/50 p-3">
            <p className="text-xs font-medium text-slate-400">Conversions</p>
            <p className="text-lg font-semibold text-white">1.2K</p>
            <span className="text-xs font-medium text-emerald-500">+8.1%</span>
          </div>
        </div>
        <div className="mb-4 h-24 w-full overflow-hidden rounded-lg bg-slate-900/50 p-3">
          <div className="flex h-full w-full items-end justify-between gap-1">
            <div className="h-[40%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[60%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[60%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[40%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[75%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[80%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[45%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[50%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[85%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[90%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[65%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[70%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
            <div className="h-[95%] w-3 rounded-sm bg-indigo-500/30">
              <div className="h-[85%] w-full rounded-sm bg-indigo-500 transition-all duration-300" />
            </div>
          </div>
        </div>
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <span className="text-xs font-medium text-slate-400">Last 7 days</span>
            <svg className="h-4 w-4 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </div>
          <button className="flex items-center gap-1 rounded-lg bg-gradient-to-r from-indigo-500 to-purple-500 px-3 py-1 text-xs font-medium text-white transition-all duration-300 hover:from-indigo-600 hover:to-purple-600">
            View Details
            <svg className="h-3 w-3" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
}
