import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import './Login.css';

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // Vérifier compte dans localStorage (simple)
    try {
      const accountsRaw = localStorage.getItem('accounts');
      const accounts = accountsRaw ? JSON.parse(accountsRaw) : [];
      const found = accounts.find(a => a.email === email && a.password === password);
      if (found) {
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('currentUser', email);
        navigate('/fiche');
        return;
      }
    } catch (err) {}
    alert('Email ou mot de passe incorrect');
  };

  return (
    <div style={{display:'flex',justifyContent:'center',alignItems:'center',minHeight:'100vh'}}>
  <div className="form-container">
        <h2 className="title">Se connecter</h2>
        <form className="form" onSubmit={handleLogin}>
          <input className="input" placeholder="Email" type="email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <input className="input" placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required />
          <div className="page-link"><label className="page-link-label">Mot de passe oublié?</label></div>
          <button className="form-btn" type="submit">Se connecter</button>
          <p style={{display:'flex',justifyContent:'space-between',alignItems:'center'}}>
            <span className="sign-up-label">Pas de compte ? <span className="sign-up-link" onClick={()=>navigate('/')}>Créer un compte</span></span>
            <Link to="/" className="page-link-label">Accueil</Link>
          </p>
        </form>
      </div>
    </div>
  );
}
