import { Link, useNavigate } from "react-router-dom";
import './style/bouton.css';
import './Accueil.css';
import { useState } from 'react';
import { hashPwd } from './utils';

export default function Accueil() {
  const navigate = useNavigate();
  const [first, setFirst] = useState('');
  const [last, setLast] = useState('');
  const [email, setEmail] = useState('');
  const [pwd, setPwd] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const raw = localStorage.getItem('accounts');
      const accounts = raw ? JSON.parse(raw) : [];
      if (accounts.find(a => a.email === email)) {
        alert('Un compte existe déjà avec cet email');
        return;
      }
  const hashed = await hashPwd(pwd);
      accounts.push({ email, password: hashed, first, last, isAdmin: false, isVerified: false });
      localStorage.setItem('accounts', JSON.stringify(accounts));
      // call server to send verification code by email
      try {
        const res = await fetch((process.env.REACT_APP_API_URL || '') + '/api/send-code', {
          method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ email })
        });
        const body = await res.json();
        localStorage.setItem('pendingVerification', email);
        if (body.code) {
          // dev mode: server returned code in response
          alert('Code envoyé (dev): ' + body.code + '\nVous allez être redirigé vers la page de vérification.');
        } else {
          alert('Code de vérification envoyé à votre email. Vous allez être redirigé vers la page de vérification.');
        }
        navigate('/verify');
      } catch (err) {
        console.error(err);
        alert('Impossible d\'envoyer le code de vérification');
      }
    } catch (err) {
      console.error(err);
      alert('Impossible de créer le compte');
    }
  };

  return (
    <div style={{ display: 'flex', gap: 40, padding: 40, alignItems: 'flex-start', justifyContent: 'center' }}>
      <div style={{ textAlign: "center", maxWidth: 520 }}>
        <h1>Bienvenue sur le système de Fiche de Paie</h1>
        <p>Choisissez une action :</p>
        <div style={{ marginTop: "20px" }}>
          <div className="btn-group">
            <Link to="/fiche" className="btn primary" data-text="Créer une fiche">Créer une fiche</Link>
            
          </div>
        </div>
      </div>

      <div>
    {/* Formulaire d'inscription importé depuis Uiverse */}
    <form className="form" onSubmit={handleRegister}>
          <p className="title">Register</p>
          <p className="message">Signup now and get full access to our app.</p>
          <div className="flex">
            <label>
      <input className="input" type="text" placeholder="" required value={first} onChange={e=>setFirst(e.target.value)} />
              <span>Firstname</span>
            </label>
            <label>
      <input className="input" type="text" placeholder="" required value={last} onChange={e=>setLast(e.target.value)} />
              <span>Lastname</span>
            </label>
          </div>

          <label>
            <input className="input" type="email" placeholder="" required value={email} onChange={e=>setEmail(e.target.value)} />
            <span>Email</span>
          </label>

          <label>
            <input className="input" type="password" placeholder="" required value={pwd} onChange={e=>setPwd(e.target.value)} />
            <span>Password</span>
          </label>

          <label>
            <input className="input" type="password" placeholder="" required />
            <span>Confirm password</span>
          </label>

          <button className="submit" type="submit">Submit</button>
          <p className="signin">Already have an acount ? <a href="#">Signin</a> </p>
        </form>
      </div>
    </div>
  );
}
