import { Link, useNavigate } from "react-router-dom";
import './styles/bouton.css';
import './styles/Accueil.css';
import accueilImg from './Accueil.jpg';
import { useState } from 'react';
import { hashPwd } from './utils';

export default function Accueil() {
  const navigate = useNavigate();
  const [first, setFirst] = useState('');
  const [last, setLast] = useState('');
  const [email, setEmail] = useState('');
  const [pwd, setPwd] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const raw = localStorage.getItem('accounts');
      const accounts = raw ? JSON.parse(raw) : [];
      if (accounts.find(a => a.email === email)) {
        alert('Un compte existe déjà avec cet email');
        return;
      }
  const hashed = await hashPwd(pwd);
  // create account and mark as verified immediately (no email verification)
  accounts.push({ email, password: hashed, first, last, isAdmin: false, isVerified: true });
  localStorage.setItem('accounts', JSON.stringify(accounts));
  alert('Compte créé — vous pouvez vous connecter');
  navigate('/login');
    } catch (err) {
      console.error(err);
      alert('Impossible de créer le compte');
    }
  };

  return (
    <div>
  <header style={{position:'relative', height:420, backgroundImage:`linear-gradient(rgba(255,255,255,0.8), rgba(255,255,255,0.4)), url(${accueilImg})`, backgroundSize:'cover', backgroundPosition:'center'}}>
        <div style={{maxWidth:1100, margin:'0 auto', padding:20, display:'flex', alignItems:'center', justifyContent:'space-between'}}>
          <div style={{display:'flex',alignItems:'center',gap:12}}>
            <div style={{width:80,height:80,borderRadius:'50%',background:'#fff',display:'flex',alignItems:'center',justifyContent:'center',boxShadow:'0 2px 8px rgba(0,0,0,0.1)'}}>
              <span style={{fontWeight:700,color:'#333'}}>O</span>
            </div>
            <div>
              <h1 style={{margin:0,fontSize:28}}>Paie</h1>
              <div style={{fontSize:14,color:'#333'}}>Accompagnement • et conseil</div>
            </div>
          </div>

          <nav>
            <ul style={{display:'flex',gap:18,listStyle:'none',margin:0,padding:0}}>
              <li><a href="#" style={{color:'#5c5959ff',textDecoration:'none'}}>Bienvenue</a></li>
              <li><a href="#" style={{color:'#5c5959ff',textDecoration:'none'}}>Mes prestations</a></li>
              <li><a href="#" style={{color:'#5c5959ff',textDecoration:'none'}}>Contact</a></li>
              <li><a href="/login" style={{color:'#5c5959ff',textDecoration:'none'}}>Login</a></li>
            </ul>
          </nav>
        </div>

        <div style={{maxWidth:1100, margin:'0 auto', padding:20, marginTop:40}}>
          <h2 style={{fontSize:44, color:'#fff', marginBottom:6}}>Paie Malagasy</h2>
          <p style={{color:'#fff',opacity:0.9, maxWidth:700}}>Accompagnement — Soutien et conseil pour les entreprise</p>
        </div>
      </header>

      <main style={{maxWidth:1100, margin:'-60px auto 60px', padding:20}}>
        <section style={{display:'flex',gap:40}}>
          <div style={{flex:1}}>
            {/* Formulaire d'inscription */}
            <form className="form" onSubmit={handleRegister}>
              <p className="title">Créer un compte</p>
              <div className="flex">
                <label>
                  <input className="input" type="text" placeholder="" required value={first} onChange={e=>setFirst(e.target.value)} />
                  <span>Firstname</span>
                </label>
                <label>
                  <input className="input" type="text" placeholder="" required value={last} onChange={e=>setLast(e.target.value)} />
                  <span>Lastname</span>
                </label>
              </div>
              <label>
                <input className="input" type="email" placeholder="" required value={email} onChange={e=>setEmail(e.target.value)} />
                <span>Email</span>
              </label>
              <label>
                <input className="input" type="password" placeholder="" required value={pwd} onChange={e=>setPwd(e.target.value)} />
                <span>Password</span>
              </label>
              <button className="submit" type="submit">Créer un compte</button>
            </form>
          </div>

          <div style={{width:380}}>
            <div className="section">
              <h3>Mes prestations</h3>
              <p style={{color:'#666'}}>Contenu à venir.</p>
            </div>
            <div className="section" style={{marginTop:12}}>
              <h3>Contact</h3>
              <p style={{color:'#666'}}>Contenu à venir.</p>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}
