import { useMemo, useState } from "react";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";

const toAr = n =>
  (Math.round(Number(n) || 0))
    .toLocaleString("fr-FR")
    .replace(/\u202F/g, " ");

export default function App() {
  const [logoDataUrl, setLogoDataUrl] = useState(null);
  const [logoColor, setLogoColor] = useState("#1976d2");

  const [rows, setRows] = useState([]);

  const [f, setF] = useState({
    employeur_nom: "",
    employeur_adresse: "",
    employeur_nif: "",
    employeur_stat: "",
    employeur_cnaps: "",
    nom: "",
    prenom: "",
    cin: "",
    matricule: "",
    poste: "",
    date_embauche: "",
    mois: "",
    annee: "",
    salaire_base: "",
    heures_sup: "",
    prime: "",
    indemnite: "",
    avantages: "",
    retenues: "",
    nb_enfants: ""
  });

  const hints = {
    salaire_base: "Montant mensuel de base (Ar). Ex: 400000",
    heures_sup: "Montant total des heures sup (Ar). Ex: 25000",
    prime: "Primes (transport, rendement, assiduité...)", 
    indemnite: "Indemnités (logement, panier, déplacement...)", 
    avantages: "Avantages en nature (logement, voiture, téléphone...)", 
    retenues: "Retenues (avances, prêts, pénalités...)", 
    nb_enfants: "Nombre d'enfants à charge (max 4 pour abattement IRSA)"
  };

  const calc = useMemo(() => {
    const salaire_base = +f.salaire_base || 0;
    const heures_sup = +f.heures_sup || 0;
    const prime = +f.prime || 0;
    const indemnite = +f.indemnite || 0;
    const avantages = +f.avantages || 0;
    const retenues = +f.retenues || 0;
    const nb_enfants = Math.min(+f.nb_enfants || 0, 4);

    const brut = salaire_base + heures_sup + prime + indemnite + avantages;
    const cnaps = brut * 0.01;
    const ostie = brut * 0.01;
    const abattement = nb_enfants * 2000;

    let baseIrsa = Math.max(brut - abattement, 0);
    let irsa = 0;
    if (baseIrsa > 600000) { irsa += (baseIrsa - 600000) * 0.2; baseIrsa = 600000; }
    if (baseIrsa > 500000) { irsa += (baseIrsa - 500000) * 0.15; baseIrsa = 500000; }
    if (baseIrsa > 400000) { irsa += (baseIrsa - 400000) * 0.10; baseIrsa = 400000; }
    if (baseIrsa > 350000) { irsa += (baseIrsa - 350000) * 0.05; }
    irsa = Math.max(irsa, 3000);

    const net = brut - cnaps - ostie - irsa - retenues;
    return { brut, cnaps, ostie, irsa, net, abattement };
  }, [f]);

  const reset = () =>
    setF({
      employeur_nom: "",
      employeur_adresse: "",
      employeur_nif: "",
      employeur_stat: "",
      employeur_cnaps: "",
      nom: "",
      prenom: "",
      cin: "",
      matricule: "",
      poste: "",
      date_embauche: "",
      mois: "",
      annee: "",
      salaire_base: "",
      heures_sup: "",
      prime: "",
      indemnite: "",
      avantages: "",
      retenues: "",
      nb_enfants: ""
    });

  const addRow = () => {
    const r = { ...f, ...calc };
    setRows(prev => [r, ...prev]);
    reset();
  };

  const totalMasse = rows.reduce((s, r) => s + (r.net || 0), 0);

  const exportPDF = (r) => {
    const doc = new jsPDF({ unit: "pt", format: "a4" });
    const marginX = 40;
    const marginY = 36;
    const mainColor = logoColor || "#1976d2";

    // LOGO
    if (logoDataUrl) {
      doc.addImage(logoDataUrl, "PNG", marginX, marginY - 30, 50, 50);
      doc.setLineWidth(2);
      if (mainColor.startsWith("rgb")) {
        const rgb = mainColor.match(/\d+/g);
        doc.setDrawColor(Number(rgb[0]), Number(rgb[1]), Number(rgb[2]));
      } else {
        doc.setDrawColor(mainColor);
      }
      doc.circle(marginX + 25, marginY - 5, 25);
    }

    // HEADER
    doc.setFontSize(14);
    doc.setFillColor(230, 230, 230);
    doc.rect(marginX, marginY + 30, 250, 90, "F");
    doc.rect(marginX + 260, marginY + 30, 250, 90, "F");

    doc.setFontSize(12);
    doc.text("FICHE DE PAIE", marginX + 10, marginY + 50);

    doc.setFontSize(10);
    doc.text("Période", marginX + 10, marginY + 70);
    doc.text(`${r.mois || "-"} / ${r.annee || "-"}`, marginX + 80, marginY + 70);
    doc.text("N° CNAPS", marginX + 10, marginY + 90);
    doc.text(`${r.employeur_cnaps || "-"}`, marginX + 80, marginY + 90);

    doc.text(r.employeur_nom || "Société", marginX + 270, marginY + 50);
    doc.text(`Adresse: ${r.employeur_adresse || "-"}`, marginX + 270, marginY + 70);
    doc.text(`STAT: ${r.employeur_stat || "-"}`, marginX + 270, marginY + 90);
    doc.text(`NIF: ${r.employeur_nif || "-"}`, marginX + 370, marginY + 90);

    // Infos employé
    doc.text("Nom & Prénom:", marginX + 10, marginY + 120);
    doc.text(`${r.nom || "-"} ${r.prenom || "-"}`, marginX + 100, marginY + 120);
    doc.text("Matricule:", marginX + 10, marginY + 135);
    doc.text(`${r.matricule || "-"}`, marginX + 100, marginY + 135);
    doc.text("Poste:", marginX + 10, marginY + 150);
    doc.text(`${r.poste || "-"}`, marginX + 100, marginY + 150);
    doc.text("Date embauche:", marginX + 10, marginY + 165);
    doc.text(`${r.date_embauche || "-"}`, marginX + 100, marginY + 165);

    // TABLE
    const tableY = marginY + 190;
    autoTable(doc, {
      startY: tableY,
      theme: "grid",
      styles: { fontSize: 9, cellPadding: 3 },
      head: [
        [
          "Code",
          "Libellé",
          "Base salarié",
          "Taux salarié",
          "Montant salarié",
          "Base employeur",
          "Taux employeur",
          "Montant employeur"
        ]
      ],
      body: [
        ["1001", "Salaire de base", toAr(r.salaire_base), "", toAr(r.salaire_base), "", "", ""],
        ["2001", "Heures supplémentaires", toAr(r.heures_sup), "", toAr(r.heures_sup), "", "", ""],
        ["3001", "Primes", toAr(r.prime), "", toAr(r.prime), "", "", ""],
        ["3002", "Indemnités", toAr(r.indemnite), "", toAr(r.indemnite), "", "", ""],
        ["3003", "Avantages", toAr(r.avantages), "", toAr(r.avantages), "", "", ""],
        ["BRUT", "Salaire brut", toAr(r.brut), "", toAr(r.brut), "", "", ""],
        ["ST01", "OSTIE", "", "1%", toAr(r.ostie), "", "1%", toAr(r.ostie)],
        ["RT01", "CNAPS", "", "1%", toAr(r.cnaps), "", "1%", toAr(r.cnaps)],
        ["RET", "Retenues diverses", "", "", `-${toAr(r.retenues)}`, "", "", ""],
        ["IRSA", "Impôt sur salaire", "", "", `-${toAr(r.irsa)}`, "", "", ""],
        ["NET", "Net à payer", "", "", toAr(r.net), "", "", ""]
      ]
    });

    // FOOTER
    const y = Math.max(doc.lastAutoTable.finalY + 30, 760);
    doc.setFontSize(10);
    doc.text("Employeur", marginX, y);
    doc.text("Employé", 500, y);

    doc.save(`fiche_paie_${(r.nom || "")}_${(r.prenom || "")}.pdf`);
  };

  const onLogoChange = (e) => {
    const file = e.target.files?.[0];
    if (!file) {
      setLogoDataUrl(null);
      setLogoColor("#1976d2");
      return;
    }
    const reader = new FileReader();
    reader.onload = () => {
      setLogoDataUrl(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const set = (k) => (e) => setF(s => ({ ...s, [k]: e.target.value }));

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-6">
      <h1 className="text-2xl font-bold">Bulletin de paie — Madagascar</h1>

      {/* UPLOAD LOGO */}
      <div className="section">
        <input type="file" accept="image/*" onChange={onLogoChange} />
        {logoDataUrl && <img src={logoDataUrl} alt="logo" width="64" />}
      </div>

      {/* FORMULAIRE */}
      <div className="grid md:grid-cols-2 gap-4">
        <div>
          <label>Salaire de base</label>
          <input type="number" value={f.salaire_base} onChange={set("salaire_base")} />
        </div>
        <div>
          <label>Heures sup</label>
          <input type="number" value={f.heures_sup} onChange={set("heures_sup")} />
        </div>
        <div>
          <label>Primes</label>
          <input type="number" value={f.prime} onChange={set("prime")} />
        </div>
        <div>
          <label>Indemnités</label>
          <input type="number" value={f.indemnite} onChange={set("indemnite")} />
        </div>
      </div>

      <button onClick={addRow}>Ajouter fiche</button>

      {/* DASHBOARD */}
      <table>
        <thead>
          <tr>
            <th>Nom</th><th>Poste</th><th>Brut</th><th>Net</th><th>PDF</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => (
            <tr key={i}>
              <td>{r.nom} {r.prenom}</td>
              <td>{r.poste}</td>
              <td>{toAr(r.brut)}</td>
              <td>{toAr(r.net)}</td>
              <td><button onClick={() => exportPDF(r)}>Export</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      <p>Total masse salariale nette : {toAr(totalMasse)} Ar</p>
    </div>
  );
}
