import { useState } from "react";
import jsPDF from "jspdf";

export default function App() {
  const [employes, setEmployes] = useState([]);
  const [form, setForm] = useState({
    employeur_nom: "", employeur_adresse: "", employeur_nif: "", employeur_stat: "", employeur_cnaps: "",
    nom: "", prenom: "", cin: "", matricule: "", poste: "", date_embauche: "",
    mois: "", annee: "", salaire_base: 0, heures_sup: 0, primes: 0, avantages: 0, retenues: 0, nb_enfants: 0
  });

  const calculPaie = (emp) => {
    const salaireBrut = Number(emp.salaire_base) + Number(emp.heures_sup) + Number(emp.primes) + Number(emp.avantages);
    const cnaps = salaireBrut * 0.01;
    const ostie = salaireBrut * 0.01;
    const abattement = Math.min(emp.nb_enfants, 4) * 2000;
    let baseIrsa = salaireBrut - abattement;
    let irsa = 0;

    if (baseIrsa > 600000) { irsa += (baseIrsa - 600000) * 0.2; baseIrsa = 600000; }
    if (baseIrsa > 500000) { irsa += (baseIrsa - 500000) * 0.15; baseIrsa = 500000; }
    if (baseIrsa > 400000) { irsa += (baseIrsa - 400000) * 0.1; baseIrsa = 400000; }
    if (baseIrsa > 350000) { irsa += (baseIrsa - 350000) * 0.05; }

    irsa = Math.max(irsa, 3000);
    const salaireNet = salaireBrut - cnaps - ostie - irsa - Number(emp.retenues);
    return { salaireBrut, cnaps, ostie, irsa, salaireNet };
  };

  const ajouterEmploye = () => {
    const calcul = calculPaie(form);
    setEmployes([...employes, { ...form, ...calcul }]);
    setForm({ employeur_nom: "", employeur_adresse: "", employeur_nif: "", employeur_stat: "", employeur_cnaps: "",
              nom: "", prenom: "", cin: "", matricule: "", poste: "", date_embauche: "",
              mois: "", annee: "", salaire_base: 0, heures_sup: 0, primes: 0, avantages: 0, retenues: 0, nb_enfants: 0 });
  };

  const exportPDF = (emp) => {
    const doc = new jsPDF();
    const img = new Image();
    img.src = "/logo.png";
    img.onload = () => {
      doc.addImage(img, "PNG", 10, 10, 40, 20);
      doc.setFontSize(14);
      doc.text(`Fiche de paie - ${emp.nom} ${emp.prenom}`, 60, 20);
      doc.setFontSize(12);
      doc.text(`Employeur: ${emp.employeur_nom}`, 10, 40);
      doc.text(`Poste: ${emp.poste}`, 10, 50);
      doc.text(`PÃ©riode: ${emp.mois}/${emp.annee}`, 10, 60);
      doc.text(`Salaire Brut: ${emp.salaireBrut} Ar`, 10, 70);
      doc.text(`CNAPS: ${emp.cnaps} Ar`, 10, 80);
      doc.text(`OSTIE: ${emp.ostie} Ar`, 10, 90);
      doc.text(`IRSA: ${emp.irsa} Ar`, 10, 100);
      doc.text(`Salaire Net: ${emp.salaireNet} Ar`, 10, 110);
      doc.save(`fiche_paie_${emp.nom}_${emp.prenom}.pdf`);
    };
  };

  const masseSalariale = employes.reduce((sum, e) => sum + e.salaireNet, 0);

  return (
    <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-4">
      <div className="p-4 border rounded space-y-2">
        <h2 className="text-xl font-bold mb-2">Nouvelle fiche de paie</h2>
        {Object.keys(form).map((key, i) => (
          <input key={i} placeholder={key} value={form[key]} onChange={(e)=>setForm({...form, [key]:e.target.value})} className="border p-2 w-full"/>
        ))}
        <button onClick={ajouterEmploye} className="bg-blue-600 text-white p-2 rounded w-full mt-2">Ajouter fiche de paie</button>
      </div>
      <div className="p-4 border rounded">
        <h2 className="text-xl font-bold mb-4">Dashboard Paie</h2>
        <p className="mb-4 font-bold">Masse salariale nette : {masseSalariale.toLocaleString()} Ar</p>
        <table className="w-full border text-sm">
          <thead><tr className="bg-gray-200"><th>Nom</th><th>Poste</th><th>Salaire Net</th><th>PDF</th></tr></thead>
          <tbody>
            {employes.map((e, i) => (
              <tr key={i} className="border">
                <td>{e.nom} {e.prenom}</td>
                <td>{e.poste}</td>
                <td>{e.salaireNet.toLocaleString()} Ar</td>
                <td><button onClick={()=>exportPDF(e)} className="bg-green-600 text-white px-2 py-1 rounded">Export</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
